const deliverables = [];
const sequences = [];
const elements = [];

for (const deliverable of $json.final_deliverables) {
  // Collect sequence IDs for this deliverable
  const sequenceIds = [];
  for (const sequence of deliverable.sequences) {
    // Collect element IDs for this sequence
    const elementIds = [];
    for (const element of sequence.elements) {
      // Store element object with all its fields
      elements.push({ ...element });
      elementIds.push(element.element_id);
    }
    // Store sequence object with all its fields, but elements replaced by elementIds
    const { elements: _omit, ...sequenceFields } = sequence;
    sequences.push({
      ...sequenceFields,
      elements: elementIds,
    });
    sequenceIds.push(sequence.sequence_id);
  }
  // Store deliverable object with all its fields, but sequences replaced by sequenceIds
  const { sequences: _omit, ...deliverableFields } = deliverable;
  deliverables.push({
    ...deliverableFields,
    sequences: sequenceIds,
  });
}

return [
  { json: { elements } },
  { json: { sequences } },
  { json: { deliverables } },
];

